import Banner from "../components/Banner";

export default function Home() {
  return (
    <div>
      <Banner
        title="Welcome to 四國之旅"
        subtitle="踏上屬於你的日本四國冒險"
        imageUrl="/images/banner-home.jpeg"
      />
      <div className="mt-6 space-y-4 text-gray-700">
        <p>本網站記錄了我在日本四國地區旅行的體驗，包括高松、德島、愛媛等地的景點、美食與人文風景。</p><p>請透過上方導覽列，探索每個城市的故事，並感受我在旅途中捕捉的瞬間。</p>
      </div>
    </div>
  );
}
