export default function CityLayout({ imageUrl, imageAlt, region, title, paragraphs }) {
    return (
      <div className="bg-[#fdf6ef] py-12 px-4 font-serif">
        <div className="max-w-6xl mx-auto flex flex-col md:flex-row gap-10 items-center md:items-start min-h-[360px]">
          {/* 左圖 */}
          <img
            src={imageUrl}
            alt={imageAlt}
            className="rounded-lg shadow-md w-full max-h-[360px] object-contain"
          />

          {/* 右文 */}
          <div className="text-[#504339] flex-1 self-center">
            <h3 className="text-xl mb-2">{region}</h3>
            <h2 className="text-4xl font-bold tracking-wide mb-6">{title}</h2>
            {paragraphs.map((p, idx) => (
              <p key={idx} className={`text-lg leading-relaxed ${idx > 0 ? 'mt-4' : ''}`}>
                {p}
              </p>
            ))}
          </div>
        </div>
      </div>
    );
  }
