export default function Banner({ title, subtitle, imageUrl }) {
  return (
    <div
      className="w-full h-64 bg-cover bg-center rounded-lg shadow"
      style={{ backgroundImage: `url(${imageUrl})` }}
    >
      <div className="bg-black bg-opacity-50 h-full flex items-center justify-center flex-col text-white text-center">
        <h2 className="text-3xl font-bold">{title}</h2>
        <p className="mt-2 text-lg">{subtitle}</p>
      </div>
    </div>
  );
}
