import { useEffect, useState } from "react";
import { ArrowUp } from "lucide-react";

export default function SiteFooter({
  authorImage,
  authorName = "About Here",
  authorDescription,
  bannerImage,
  bannerAlt = "",
}) {
  const [showButton, setShowButton] = useState(false);

  // 平滑滾回頂部
  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  // 監聽捲動距離
  useEffect(() => {
    const handleScroll = () => {
      setShowButton(window.scrollY > 200);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <footer className="relative bg-[#00403C] text-white font-serif mt-16">
      {/* 橫幅圖片 */}
      {bannerImage && (
        <img
          src={bannerImage}
          alt={bannerAlt}
          className="w-full h-[200px] object-cover object-center"
        />
      )}

      {/* 作者介紹區塊 */}
      <div className="max-w-4xl mx-auto px-4 py-6 flex flex-col sm:flex-row items-center gap-4">
        <img
          src={authorImage}
          alt={authorName}
          className="w-20 h-20 rounded-md object-cover"
        />
        <div className="text-center sm:text-left text-sm leading-relaxed">
          <h3 className="text-lg font-semibold mb-1">{authorName}</h3>
          <p className="whitespace-pre-line">{authorDescription}</p>
        </div>
      </div>

      {/* 回到最上按鈕（200px 後才顯示） */}
      {showButton && (
        <button
          onClick={scrollToTop}
          aria-label="Back to top"
          className="fixed bottom-6 right-6 bg-white text-[#00403C] rounded-full p-2 shadow-md hover:scale-110 transition-all z-50"
        >
          <ArrowUp size={20} />
        </button>
      )}
    </footer>
  );
}
