import { Link, useLocation } from "react-router-dom";

export default function Navbar() {
  const { pathname } = useLocation();

  const links = [
    { path: "/", label: "Home" },
    { path: "/takamatsu", label: "Takamatsu" },
    { path: "/tokushima", label: "Tokushima" },
    { path: "/ehime", label: "Ehime" },
    { path: "/food", label: "Food" },
  ];

  return (
    <nav className="bg-white shadow fixed top-0 w-full z-50">
      <div className="max-w-6xl mx-auto px-4 py-3 flex justify-between items-center">
        <h1 className="text-xl font-bold text-gray-800">Goshikoku</h1>
        <ul className="flex space-x-4">
          {links.map(({ path, label }) => (
            <li key={path}>
              <Link
                to={path}
                className={`hover:text-blue-500 transition ${pathname === path ? "font-semibold text-blue-600" : "text-gray-600"}`}
              >
                {label}
              </Link>
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
}
