import { useEffect, useState } from "react";
import { ArrowUp } from "lucide-react";

export default function SiteFooter({
  authorImage,
  authorName = "About Here",
  authorQuote = "旅行不必總有驚喜，能記得，就是風景。",
  bannerImage,
  bannerAlt = "Seto Inland Sea",
}) {
  const [showButton, setShowButton] = useState(false);

  // 滾動偵測
  useEffect(() => {
    const handleScroll = () => {
      setShowButton(window.scrollY > 200);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // 平滑捲回頂部
  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <footer
      className="relative h-[280px] bg-cover bg-center text-white font-serif mt-16"
      style={{ backgroundImage: `url(${bannerImage})` }}
    >
      {/* 遮罩層 */}
      <div className="absolute inset-0 bg-black/40" />

      {/* 作者介紹區塊 */}
      <div className="relative z-10 h-full flex flex-col justify-center items-center text-center px-4">
        <img
          src={authorImage}
          alt={authorName}
          className="w-16 h-16 rounded-full object-cover mb-3 border-2 border-white shadow-md"
        />
        <h3 className="text-lg font-bold tracking-wide">{authorName}</h3>
        <p className="text-sm mt-2 max-w-md leading-relaxed opacity-90">
          {authorQuote}
        </p>
      </div>

      {showButton && (
        <button
          onClick={scrollToTop}
          aria-label="Back to top"
          className="fixed bottom-6 right-6 bg-white text-[#00403C] rounded-full p-2 shadow-md hover:scale-110 transition-all z-50"
        >
          <ArrowUp size={20} />
        </button>
      )}
    </footer>
  );
}
