import Banner from "../components/Banner";
import { Link } from "react-router-dom";

const navCards = [
  {
    name: "Takamatsu",
    image: "/images/nav-takamatsu.jpg",
    link: "/takamatsu",
  },
  {
    name: "Tokushima",
    image: "/images/nav-tokushima.jpg",
    link: "/tokushima",
  },
  {
    name: "Ehime",
    image: "/images/nav-ehime.jpg",
    link: "/ehime",
  },
];

export default function Home() {
  return (
    <div>
      <Banner
        title="SHIKOKU"
        subtitle="沒有驚喜萬分，卻讓人難以忘懷。"
        imageUrl="/images/home-banner.jpeg"
      />

      <div className="max-w-5xl mx-auto px-4 mt-6 space-y-4 text-gray-700 text-center">
        <p>本網站記錄了我在日本四國地區旅行的體驗，包括高松、德島、愛媛等地的景點、美食與人文風景。</p>
        <p>請透過上方導覽列，探索每個城市的故事，並感受我在旅途中捕捉的瞬間。</p>
      </div>
    </div>
  );
}
