export default function SiteFooter({
    authorImage = "/images/me.jpg",
    authorName = "About Here",
    authorQuote = "旅行不必總有驚喜，能記得，就是風景。",
    bannerImage = "/images/footer-banner.jpg",
    bannerAlt = "Seto Inland Sea",
  }) {
    return (
      <footer
        className="relative h-[280px] bg-cover bg-center text-white font-serif"
        style={{ backgroundImage: `url(${bannerImage})` }}
      >
        {/* 漸層遮罩（可改顏色） */}
        <div className="absolute inset-0 bg-black/40" />

        {/* 內容區塊（文字浮出） */}
        <div className="relative z-10 h-full flex flex-col justify-center items-center text-center px-4">
          <img
            src={authorImage}
            alt={authorName}
            className="w-16 h-16 rounded-full object-cover mb-3 border-2 border-white shadow-md"
          />
          <h3 className="text-lg font-bold tracking-wide">{authorName}</h3>
          <p className="text-sm mt-2 max-w-md leading-relaxed opacity-90">
            {authorQuote}
          </p>
        </div>

        {/* 回到最上按鈕（一直顯示 + 可改條件） */}
        <button
          onClick={() => window.scrollTo({ top: 0, behavior: "smooth" })}
          aria-label="Back to top"
          className="absolute bottom-4 right-4 bg-white text-[#00403C] rounded-full p-2 shadow-md hover:scale-110 transition-all z-20"
        >
          ↑
        </button>
      </footer>
    );
  }
