export default function Banner({ title, subtitle, imageUrl }) {
  return (
    <div
      className="w-full h-[250px] bg-cover bg-center"
      style={{ backgroundImage: `url(${imageUrl})` }}
    >
      <div className="h-full bg-black/30 flex flex-col justify-center items-center text-center px-4">
        <h1 className="text-5xl font-serif text-white drop-shadow">{title}</h1>
        <p className="text-lg text-white mt-2 tracking-wide drop-shadow">{subtitle}</p>
      </div>
    </div>
  );
}
