export default function ItinerarySection({ title, items }) {
    return (
      <section className="mt-12">
        <h2 className="text-2xl font-bold text-[#504339] mb-6 font-serif">{title}</h2>

        {items.map((item, idx) => (
          <div key={idx} className="mb-10">
            <h3 className="text-lg font-semibold text-[#504339] mb-2">{item.title}</h3>
            {item.date && <p className="text-sm text-gray-500 mb-2">{item.date}</p>}
            <p className="text-sm text-gray-700 mb-4">{item.description}</p>

            {/* ✅ 瀑布流圖片區 */}
            <div className="columns-1 sm:columns-2 lg:columns-3 gap-3 space-y-3">
              {(item.images || []).map((img, i) => (
                <img
                  key={i}
                  src={img}
                  alt={`${item.title} ${i + 1}`}
                  className="w-full rounded-lg shadow-sm break-inside-avoid"
                />
              ))}
            </div>
          </div>
        ))}
      </section>
    );
  }
