import { useState } from "react";
import foodData from "../data/foodData";
import { useKeenSlider } from "keen-slider/react";
import { ChevronLeft, ChevronRight } from "lucide-react";
import "keen-slider/keen-slider.min.css";

// È°ûÂà•ÈôêÂÆö
const categories = ["ÂÖ®ÈÉ®", "ÊãâÈ∫µ", "ÁÉèÈæçÈ∫µ", "ÂÜ∞ÂìÅ", "ÂÖ∂‰ªñ"];
const typeMap = {
  ÊãâÈ∫µ: "üçú",
  ÁÉèÈæçÈ∫µ: "üç•",
  ÂÜ∞ÂìÅ: "üç¶",
};

export default function Food() {
  const [selected, setSelected] = useState("ÂÖ®ÈÉ®");

  const filtered = foodData
    .map((f) => ({
      ...f,
      displayType: categories.includes(f.type) ? f.type : "ÂÖ∂‰ªñ",
    }))
    .filter((f) => selected === "ÂÖ®ÈÉ®" || f.displayType === selected);

  return (
    <div className="max-w-6xl mx-auto pt-20 px-4 py-12 font-serif text-[#504339]">
      <h1 className="text-3xl font-bold mb-6">üçΩÔ∏è ÂõõÂúãÁæéÈ£üÁ∏ΩË¶Ω</h1>

      {/* ÂàÜÈ°ûÂàáÊèõ */}
      <div className="flex overflow-x-auto whitespace-nowrap gap-3 mb-6 pb-1">
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => setSelected(cat)}
            className={`px-4 py-1 rounded-full border shrink-0 ${
              selected === cat
                ? "bg-[#504339] text-white"
                : "text-[#504339] border-[#ccc]"
            }`}
          >
            {typeMap[cat] || "üç¥"} {cat}
          </button>
        ))}
      </div>

      {/* Âç°ÁâáÂçÄ */}
      <div className="grid gap-6 sm:grid-cols-2 md:grid-cols-3">
        {filtered.map((food) => (
          <FoodCard key={food.id} food={food} />
        ))}
      </div>
    </div>
  );
}

function FoodCard({ food }) {
  const hasMultipleImages = food.images?.length > 1;
  const [currentSlide, setCurrentSlide] = useState(0);

  const [sliderRef, instanceRef] = useKeenSlider({
    loop: true,
    slides: {
      perView: 1,
      spacing: 10,
    },
    slideChanged(slider) {
      setCurrentSlide(slider.track.details.rel);
    },
  });

  return (
    <div className="relative bg-white p-4 rounded-xl shadow">
      <div className="relative">
        <span className="absolute -top-3 -right-3 z-10 bg-white/90 text-xs px-3 py-1 rounded-full text-gray-700 shadow">
          {typeMap[food.displayType] || "üç¥"} {food.displayType}
        </span>

        <div ref={sliderRef} className="keen-slider overflow-hidden rounded">
          {(food.images || [food.image]).map((imgObj, idx) => {
            const src = typeof imgObj === "string" ? imgObj : imgObj.src;
            const pos = typeof imgObj === "string" ? (food.imagePositionPercent ?? 50) : (imgObj.position ?? 50);
            return (
              <div key={idx} className="keen-slider__slide">
                <img
                  src={src}
                  alt={`${food.name} ${idx + 1}`}
                  className="w-full h-48 sm:h-40 object-cover"
                  style={{
                    objectPosition: `center ${100 - pos}%`,
                  }}
                />
              </div>
            );
          })}
        </div>

        {hasMultipleImages && (
          <>
            <button
              onClick={() => instanceRef.current?.prev()}
              className="absolute top-1/2 left-1 sm:left-2 transform -translate-y-1/2 bg-white/80 hover:bg-white rounded-full p-1 shadow"
            >
              <ChevronLeft size={16} className="sm:size-5" />
            </button>
            <button
              onClick={() => instanceRef.current?.next()}
              className="absolute top-1/2 right-1 sm:right-2 transform -translate-y-1/2 bg-white/80 hover:bg-white rounded-full p-1 shadow"
            >
              <ChevronRight size={16} className="sm:size-5" />
            </button>
            <div className="absolute bottom-3 sm:bottom-2 left-1/2 -translate-x-1/2 flex gap-1 z-10">
              {(food.images || [food.image]).map((_, i) => (
                <button
                  key={i}
                  onClick={() => instanceRef.current?.moveToIdx(i)}
                  className={`w-2 h-2 rounded-full ${
                    currentSlide === i ? "bg-[#504339]" : "bg-gray-300"
                  }`}
                />
              ))}
            </div>
          </>
        )}
      </div>

      <h3 className="mt-2 text-lg font-semibold">{food.name}</h3>
      <p className="text-sm text-gray-600 mb-1">{food.description}</p>
      {food.location && (
        <p className="text-xs text-gray-500">üìç {food.location}</p>
      )}
      {food.googleMapUrl && (
        <a
          href={food.googleMapUrl}
          target="_blank"
          rel="noopener noreferrer"
          className="text-xs text-blue-600 hover:underline"
        >
          Âú® Google Âú∞ÂúñÊü•Áúã ‚Üí
        </a>
      )}
    </div>
  );
}
