import CityIntro from "../components/CityIntro";
import ItinerarySection from "../components/ItinerarySection";
import FoodForDay from "../components/FoodForDay";

export default function Takamatsu() {
  return (
    <div className="bg-[#fdf6ef] font-serif text-[#504339]">
      {/* 城市介紹 */}
      <CityIntro
        title="About Takamatsu"
        subtitle="高松市"
        imageSrc="/images/takamatsu/takamatsu-ferry.jpg"
        imageAlt="Takamatsu ferry"
        paragraphs={[
          "高松，是一座被烏龍麵香氣輕輕包圍的城市...",
          "鄰近的小豆島則以橄欖聞名，在陽光與海風中散發著地中海的氣息。",
        ]}
      />

      {/* 行程 Day 1 */}
      <ItinerarySection
        title="Day 1 - 2024/12/28"
        items={[
          {
            title: "高松港搭船前往小豆島",
            description: "搭乘渡輪，開始島上探索。",
            date: "上午",
            images: ["/images/itinerary/ferry.jpg"],
          },
          {
            title: "橄欖公園拍照＋吃霜淇淋",
            description: "宮崎駿風場景、橄欖霜淇淋超棒。",
            date: "下午",
            images: ["/images/itinerary/olive-park.jpg"],
          },
        ]}
      />

      {/* 當日美食 */}
      <FoodForDay date="2024-12-28" />
    </div>
  );
}
