export default function SiteFooter({
    authorImage,
    authorName = "About Here",
    authorDescription,
    bannerImage,
    bannerAlt = "",
  }) {
    return (
      <footer className="bg-[#00403C] text-white font-serif mt-16">
        {/* 橫幅圖片 */}
        {bannerImage && (
          <img
            src={bannerImage}
            alt={bannerAlt}
            className="w-full h-[300px] object-cover object-center"
          />
        )}

        {/* 作者介紹區塊 */}
        <div className="max-w-4xl mx-auto px-4 py-12 flex flex-col sm:flex-row items-center gap-6">
          <img
            src={authorImage}
            alt={authorName}
            className="w-28 h-28 rounded-md object-cover"
          />
          <div className="text-center sm:text-left">
            <h3 className="text-xl font-bold mb-2">{authorName}</h3>
            <p className="text-sm whitespace-pre-line leading-relaxed">
              {authorDescription}
            </p>
          </div>
        </div>
      </footer>
    );
  }
