import { useState } from "react";
import foodData from "../data/foodData";

// È°ûÂà•ÈôêÂÆö
const categories = ["ÂÖ®ÈÉ®", "ÊãâÈ∫µ", "ÁÉèÈæçÈ∫µ", "ÂÜ∞ÂìÅ", "ÂÖ∂‰ªñ"];
const typeMap = {
  ÊãâÈ∫µ: "üçú",
  ÁÉèÈæçÈ∫µ: "üç•",
  ÂÜ∞ÂìÅ: "üç¶",
};

export default function Food() {
  const [selected, setSelected] = useState("ÂÖ®ÈÉ®");

  const filtered = foodData
    .map((f) => ({
      ...f,
      displayType: categories.includes(f.type) ? f.type : "ÂÖ∂‰ªñ",
    }))
    .filter((f) => selected === "ÂÖ®ÈÉ®" || f.displayType === selected);

  return (
    <div className="max-w-6xl mx-auto pt-20 px-4 py-12 font-serif text-[#504339]">
      <h1 className="text-3xl font-bold mb-6">üçΩÔ∏è ÂõõÂúãÁæéÈ£üÁ∏ΩË¶Ω</h1>

      {/* ÂàÜÈ°ûÂàáÊèõ */}
      <div className="flex flex-wrap gap-3 mb-6">
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => setSelected(cat)}
            className={`px-4 py-1 rounded-full border ${
              selected === cat
                ? "bg-[#504339] text-white"
                : "text-[#504339] border-[#ccc]"
            }`}
          >
            {typeMap[cat] || "üç¥"} {cat}
          </button>
        ))}
      </div>

      {/* Âç°ÁâáÂçÄ */}
      <div className="grid gap-6 sm:grid-cols-2 md:grid-cols-3">
        {filtered.map((food) => (
          <div key={food.id} className="relative bg-white p-4 rounded-xl shadow">
            {/* Ê®ôÁ±§ */}
            <span className="absolute top-2 right-2 bg-[#eee] text-xs px-2 py-1 rounded-full text-gray-600">
              {typeMap[food.displayType] || "üç¥"} {food.displayType}
            </span>

            <img
              src={food.image}
              alt={food.name}
              className="w-full h-40 object-cover rounded"
              style={{
                objectPosition: `center ${100 - (food.imagePositionPercent ?? 50)}%`
              }}
            />
            <h3 className="mt-2 text-lg font-semibold">{food.name}</h3>
            <p className="text-sm text-gray-600 mb-1">{food.description}</p>
            {food.location && (
              <p className="text-xs text-gray-500">üìç {food.location}</p>
            )}
            {food.googleMapUrl && (
              <a
                href={food.googleMapUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="text-xs text-blue-600 hover:underline"
              >
                Âú® Google Âú∞ÂúñÊü•Áúã ‚Üí
              </a>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
